using Accounting.Client.Extensions;
using Accounting.Shared.Upload;
using Microsoft.AspNetCore.Components.Forms;
using Radzen.Blazor;
using Microsoft.AspNetCore.Components;

namespace Accounting.Client.Components.Upload
{
    public partial class Upload
    {
        private const int maxUploadSize = 2 * 1024 * 1024; // 2 MB
        private List<UploadingFile> loadedFiles = new();
        private bool uploadingInProgress = false;
        private string labelClass = "d-visible";
        [Inject]
        public JSFunctionHandler JSFunctionHandler { get; set; } 

        private void RemoveImage(int index)
        {
            if (!string.IsNullOrEmpty(index.ToString()) && loadedFiles.Count > 0)
            {
                loadedFiles.RemoveAt(index);
            }
        }

        async Task OnChange(InputFileChangeEventArgs e)
        {
            using var multipartContent = new MultipartFormDataContent();
            var imageSource = "data:image/jpeg;base64,";
            foreach (var file in e.GetMultipleFiles())
            {
                var stream = file.OpenReadStream(file.Size);
                var contentType = file.ContentType;
                if(contentType.Contains("pdf"))
                {
                    // pdf 
                    imageSource = "data:image/jpeg;base64,";
                }
                if(contentType.Contains("zip"))
                {
                    // zip
                    imageSource = "data:image/jpeg;base64,";
                }
                if ( contentType.Contains("image"))
                {
                    // Resize image
                    var resizedFile = await file.RequestImageFileAsync(file.ContentType, 96, 70);
                    var buf = new byte[resizedFile.Size];
                    using (var streamResized = resizedFile.OpenReadStream())
                    {
                        await streamResized.ReadAsync(buf);
                    }
                    imageSource = $"data:{file.ContentType};base64,{Convert.ToBase64String(buf)}";
                }
             
                loadedFiles.Add(new UploadingFile
                {
                    ImageSource = imageSource,
                    File = file,
                    Stream = stream
                });
            }
            labelClass = "d-visible-hidden";
            uploadingInProgress = false;
        }

        private async void StartUpload()
        {
            foreach (var uploadingFile in loadedFiles)
            {
                if (uploadingFile.Done == true)
                    continue;
                else 
                    uploadingFile.Done = true;

                var multipartContent = new MultipartFormDataContent();
                // var fileStream = uploadingFile.File.OpenReadStream(5000000);
                var streamContent = new ProgressiveStreamContent(uploadingFile.Stream, 40096, (u,p) =>
                {
                    uploadingFile.Uploaded = u;
                    uploadingFile.Percentage = p;
                    StateHasChanged();
                    checkIfAllDone();
                });
                // Add to multipart form
                multipartContent.Add(streamContent, "file", uploadingFile.File.Name);
                // Post the request
                var response = httpClient.PostAsync("/api/Upload", multipartContent);
            }
            uploadingInProgress = true;
        }

        private bool checkIfAllDone()
        {
            var allDone = 0;
            foreach (var uploadingFile in loadedFiles)
            {
                if (uploadingFile.Percentage > 99)
                {
                    allDone++;
                }
            }
            if (allDone == loadedFiles.Count)
            {
                uploadingInProgress=false;
                StateHasChanged();
                return true;
            }
            return false;
        }

        private async Task OnRadzenSplitButtonClick(RadzenSplitButtonItem item)
        {
            await JSFunctionHandler.OpenFileDialog();
        }
    }
}
